package pro;

public class Fact {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int n=5;
           int f=1;
           
           for(int i=1;i<=n;i++)
           {
        	   f=f*i;
           }
           System.out.println("Factorial of given number is - "+f);
           
	}

}
